import React,{useState} from "react";

export default function Forms(props)
    {
    const Totale = props.collection.reduce((e,T)=>( T = T +e),0)
    function supprimer(e)
        {
            props.remove(e.target.value)
        }
    return (
    <div class="table-responsive">
        <table class="table table-striped text-successtable-border border-light">
        <thead class="border-light">
        <tr>
            <td scope="col"> Libelle </td>
            <td scope="col"> Prix </td>
            <td scope="col"> Quantite </td>
            <td scope="col"> Prix * QT </td>
            <td scope="col"> Action </td>
        </tr>
        </thead>
        <tbody>
        { props.collection.map((e)=>(
                <tr>
                    <td>{e.Libelle}</td>
                    <td>{e.Prix}</td>
                    <td>{e.Quantite}</td>
                    <td>{e.Quantite *e.Prix } </td>
                    <td><button value={e.Libelle} onClick={(e) => supprimer(e)}> Supprimer </button></td>
                </tr>
            
        ))}
        <tr>
            <td colSpan="2"></td>
            <td>Total</td>
            <td> {Totale}</td>
        </tr>
        </tbody>
        </table>
    </div>
    )
}
