import React, { useState,useEffect } from "react";


export default function Minuterie()
    {
        let timer = null;
        const [Afficher,setAfficher] = useState(0);
        function Start()
            {
                timer = setTimeout(() => setAfficher(Afficher + 1), 1000)
                
            }
        function Areter()
            {
                clearTimeout(timer);
            }
        return(
            <table>
                <h1> Somme: </h1>
                <tr>
                    <td></td>
                    <td>{Afficher}</td>
                </tr>
                <tr>
                    <td><input type="submit" onClick={()=>Start()} value="start"/></td>
                    <td><input type="submit" onClick={()=>Areter()} value="Arrete"/></td>
                </tr>
            </table>
        )
    }
