import React,{useState} from "react";

export default function Forms(props)
    {
        const [ID,setID] = useState("")
        const [Libelle,setLibelle] = useState("")
        const [Prix,setPrix] = useState("")
        const [Quantite, setQuantite] = useState("")
    function Add(e)
        {
            e.preventDefault();
            props.add({ID:ID,Libelle:Libelle,Prix:Prix,Quantite:Quantite})
        }
    return (
    <div class="col-md-6">
        <form onSubmit={(e)=>Add(e)}>
            <div class="mb-3">
                <label class="form-label"> ID : </label>
                <input type="text" class="form-control" onChange={(e)=>setID(e.target.value)}/><br/>
            </div>
            <div class="mb-3">
                <label class="form-label"> Libelle : </label>
                <input type="text" class="form-control"  onChange={(e)=>setLibelle(e.target.value)}/><br/>
            </div>
            <div class="mb-3">
                <label class="form-label"> Prix : </label>
                <input type="text" class="form-control" onChange={(e)=>setPrix(e.target.value)}/><br/>
            </div>
            <div class="mb-3">
                <label class="form-label"> Quantite : </label>
                <input type="text" class="form-control"  onChange={(e)=>setQuantite(e.target.value)}/><br/>
            </div>
            <input type="submit" value="Add" class="btn btn-primary"/>
        </form>
    </div>
    )
}
