import React, { useState } from 'react';
import {Link } from 'react-router-dom';
import {useDispatch} from'react-redux';
import{ADD_INSCRIPTION_action} from '../Actions/actions';

export default function AddInscr(){
    const [id,setID]=useState("")
    const [nom,setNom]=useState("")
    const [fillier,setFiliere] =useState("")
    const dispatch = useDispatch()
        function Ajouter(e)
        {
            e.preventDefault();
            dispatch(ADD_INSCRIPTION_action({id:{id},nom:{nom},fillier:{fillier}}))
        }
    return(
        <div>
            <form onSubmit={(e)=>Ajouter(e)}>
                <input type="text" onChange={(e)=>setID(e.target.value)} />
                <input type="text" onChange={(e)=>setNom(e.target.value)} />
                <input type="text" onChange={(e)=>setFiliere(e.target.value)} />
                <input type="submit" />
            </form>
            <br/><br/>
            <Link to="/">
                    return
            </Link>
        </div>
    )
}
