import React, { useState } from 'react';
import { useSelector } from "react-redux";


export default function Search(){
    const listes = useSelector(state =>state)
    const [Rechercheer,setRechercher] =useState("")
    const [Afficher,setAfficher] =useState("")
    var stagiaire = listes.filter((elm)=> ( elm.Groupe == Rechercheer || elm.Filière == Rechercheer ) )
        function Rechercher(e)
            {
                (stagiaire == "")
                ?(alert("Stagiaire pas trouver."))
                :(setAfficher(stagiaire))
            }
            
    return(
        <div>
                <input type="text" onChange={(e)=>setRechercher(e.target.value)} />
                <input type="submit" onClick={()=>Rechercher()} />
                <br/><br/>
                {(Afficher == "") 
                ?("")
                :(<>
                    <table border="1">
                    <tr>
                        <td>idstg</td>
                        <td>nom</td>
                        <td>prenom</td>
                        <td>adresse</td>
                        <td>Téléphone</td>
                        <td>Filière</td>
                        <td>Groupe</td>
                        <td>action</td>
                    </tr>
                        {listes.map((e)=>
                                        <tr>
                                            <td>{e.idstg}</td>
                                            <td>{e.nom}</td>
                                            <td>{e.prenom}</td>
                                            <td>{e.adresse}</td>
                                            <td>{e.Téléphone}</td>
                                            <td>{e.Filière}</td>
                                            <td>{e.Groupe}</td>
                                            <td> <Link to={`/UpdateStagiaire/${e.idstg}`}> <button>Update Stagiaire</button> </Link> </td>
                                        </tr>
                                        )}
                </table>
                
                </>)
                }
        </div>
    )
}
