import React from "react";
import {useDispatch} from'react-redux';
import { useSelector } from "react-redux";
import{action_increment ,action,action_decrement} from './../Actions/actions'

export default function Counter()
    {
        const compteur = useSelector(state =>state.count)
        const dispatch = useDispatch()
        function incremanter()
            {
                dispatch(action_increment())
            }
        function decremanter()
            {
                dispatch(action_decrement())
            }
        return(
            <div>
                <h2>{compteur}</h2>
                <button onClick={()=>incremanter()}>Incremanter</button>
                <button onClick={()=>decremanter()}>Decremanter</button>
            </div>
        )
    }
