export default class Exrc3 extends React.Component
    {
        constructor()
            {
                super()
                this.state = {
                    Afficher : ""
                }
            }
        render()
            {
                return(
                    <div>
                        <h1> Exr 3 : </h1>
                        Text : <input type="text" onChange={(e)=>(this.setState(e.target.value))}/>
                        <h3> Afficher : {Afficher}</h3>
                    </div>
                )
            }
    }
