import React from "react";
import {useDispatch} from'react-redux';
import{action_incremant,action_decremant} from './../Actions/actions';
import {Link } from 'react-router-dom';

export default function Counter()
    {
        const dispatch = useDispatch()
        var pas = 0;
        function incremanter()
            {
                dispatch(action_incremant(pas))
            }
        function decremanter()
            {
                dispatch(action_decremant(pas))
            }
        function Pas(e)
            {
                pas = e.target.value
            }
        return(
            <div>
                <br/>
                <input type="text" onChange={(e)=>Pas(e)}/>
                <br/>
                <button onClick={()=>incremanter()}>Incremanter</button>
                <button onClick={()=>decremanter()}>Decremanter</button>
                <br/>
                <br/>
                <Link to="/Afficher">
                        Afficher compteur
                </Link>
            </div>
        )
    }
