import{ADD_INSCRIPTION,REMOVE_INSCRIPTION} from '../Actions/actions';
const listInscription = [
							{id:1,nom:"simo",fillier:"FUS"},
							{id:2,nom:"abdou",fillier:"FUM"}
						]
export function AfficherInsc(state=listInscription,action)
{
	switch(action.type)
	{
		case ADD_INSCRIPTION :
			return [...state,action.payload];
		case REMOVE_INSCRIPTION :
			return state.filter((e)=> e.id !== action.payload.id);
		//case EDIT_INSCRIPTION :
			//return [...state.filter((e)=> e.id !== action.payload.id),{id,nom,fillier}];
		default :
			return listInscription;
	}
}
