import React,{useState} from "react";
import Forms from "./Components4/Forms";
import Liste from "./Components4/Liste"

export default function App() {
    const [liste, setList] = useState([]);

    function addelm(el)
        {
            setList([...liste, el]);
        };

    function remove(el)
        {
            setList(liste.filter((e)=> e.Libelle != el ));
        };

    return (
    <div>
        <h1>Gestion de factures</h1>
        <Forms add={addelm} />
        <Liste collection={liste} remove={remove} />
    </div>
    );
    }
