import React from "react";
import {useDispatch} from'react-redux';
import{action_incremant,action_decremant} from './../Actions/actions';
import {Link } from 'react-router-dom';

export default function Counter()
    {
        const dispatch = useDispatch()
        function incremanter(pas)
            {
                dispatch(action_incremant(pas))
            }
        function decremanter(pas)
            {
                dispatch(action_decremant(pas))
            }
        return(
            <div>
                <br/>
                <br/>
                <button onClick={()=>incremanter(3)}>Incremanter</button>
                <button onClick={()=>decremanter(2)}>Decremanter</button>
                <br/>
                <br/>
                <Link to="/Afficher">
                        Afficher compteur
                </Link>
            </div>
        )
    }
