import React,{ useEffect, useState } from "react";
import {Link } from "react-router-dom";


function Menu()
{
    const url1 = 'https://s3-ap-southeast-1.amazonaws.com/tksproduction/bmtimages/pY3BnhPQYpTxasKfx.jpeg';
        
        useEffect(() => {
            const fetchImage = async function url(){
                const response = await fetch(url1)
                const blob = await response.blob()
                
                return blob
                }
        
                const downloadImage = async function url() {
                const imageBlob = await fetchImage(url)
                const imageBase64 = URL.createObjectURL(imageBlob)
        
                console.log(imageBase64)
                return imageBase64 
                }
          }, []);
        return(
            <div>
                <Link to='/'>Menu</Link>
                <Link to='/AccueilInscription'>AccueilInscription</Link>
                <Link to='/AccueilInscription'>component2</Link>  
                <img src={downloadImage}/>              
            </div> 
        
)}
export default Menu
