import React from "react";


export default class Calculer2 extends React.Component
    {
        constructor(props)
            {
                super(props)
                this.state = {
                    a : 0,
                    b : 0,
                    OP: props.op,
                    resultat : 0
                }
            }
        Calculer()
            {
                switch(this.state.OP){
                    case '+':
                        setResultat(this.state.a + this.state.b)
                    break;
                    case '-':
                        setResultat(this.state.a - this.state.b)
                    break;
                    case '*': 
                        setResultat(this.state.a * this.state.b)
                    break;
                    case '/':
                        setResultat(this.state.a / this.state.b)
                    break;
                  };
            }
            
        render()
        {
            return(
                <table>
                    <h1> Somme: </h1>
                    <tr>
                        <td><label> a : </label></td>
                        <td><input type="text" onChange={(e)=>this.setState({ a : parseFloat(e.target.value) }) }/></td>
                    </tr>
                    <tr>
                        <td></td>
                        <td> {this.state.OP} </td>
                    </tr>
                    <tr>
                        <td><label> b : </label></td>
                        <td><input type="text" onChange={(e)=>this.setState({ b : parseFloat(e.target.value) }) }/></td>
                    </tr>
                    <tr>
                        <td><label> R : </label></td>
                        <td><input type="text" value={this.state.resultat} disabled/></td>
                    </tr>
                    <tr>
                        <td></td>
                        <td><input type="submit" onClick={()=>this.Calculer()}/></td>
                    </tr>
                </table>
            )
        }
    }
