import React, { useState } from 'react';
import {Link } from 'react-router-dom';
import {useDispatch} from'react-redux';
import{ADD_INSCRIPTION_action} from '../Actions/actions';
import { useNavigate } from 'react-router-dom';
import { useSelector } from "react-redux";

export default function AddInscr(){
    const listes = useSelector(state =>state)
    const [id,setID]=useState(listes.length+1)
    const [nom,setNom]=useState("")
    const [fillier,setFiliere] =useState("")
    const navigate = useNavigate()
    const dispatch = useDispatch()
    alert(listes.length+1)
        function Ajouter(e)
        {
            e.preventDefault();
            dispatch(ADD_INSCRIPTION_action({id:id,nom:nom,fillier:fillier}))
            alert("L'inscription bien Ajouter !")
            navigate('/')
        }
    return(
        <div>
            <form onSubmit={(e)=>Ajouter(e)}>
                <input type="text" value={id} disabled onChange={(e)=>setID(e.target.value)} />
                <input type="text" onChange={(e)=>setNom(e.target.value)} />
                <input type="text" onChange={(e)=>setFiliere(e.target.value)} />
                <input type="submit" />
            </form>
            <br/><br/>
            <Link to="/">
                    return
            </Link>
        </div>
    )
}
