import React from "react";
import {Link } from 'react-router-dom';
import {useDispatch} from'react-redux';
import{ADD_INSCRIPTION_action} from '../Actions/actions';

export default function AddInscr(){
    const dispatch = useDispatch()
        function Ajouter(e)
        {
            e.preventDefault();
            alert(e.map((elm)=>elm.id))
            dispatch(ADD_INSCRIPTION_action(e))
        }
    return(
        <div>
            <form onSubmit={(e)=>Ajouter(e)}>
                <input type="text" />
                <input type="text" />
                <input type="text" />
                <input type="submit" />
            </form>
            <br/><br/>
            <Link to="/">
                    return
            </Link>
        </div>
    )
}
