import React, { useState } from "react";


export default function Somme()
    {
        const [a,setA] = useState(0);
        const [b,setB] = useState(0);
        const [resultat,setResultat] = useState(0);
        function Calculer()
            {
                setResultat(a+b)
            }
        return(
            <table>
                <h1> Somme: </h1>
                <tr>
                    <td><label> a : </label></td>
                    <td><input type="text" onChange={(e)=>setA(parseFloat(e.target.value))}/></td>
                </tr>
                <tr>
                    <td></td>
                    <td> + </td>
                </tr>
                <tr>
                    <td><label> b : </label></td>
                    <td><input type="text" onChange={(e)=>setB(parseFloat(e.target.value))}/></td>
                </tr>
                <tr>
                    <td><label> R : </label></td>
                    <td><input type="text" value={resultat} disabled/></td>
                </tr>
                <tr>
                    <td></td>
                    <td><input type="submit" onClick={()=>Calculer()}/></td>
                </tr>
            </table>
        )
    }
