import React from "react";
import {useDispatch} from'react-redux';
import{action_incremant,action_decremant} from '../Actions/actions';
import {Link } from 'react-router-dom';
import { useSelector } from "react-redux";

export default function Counter()
    {
        const listes = useSelector(state =>state)
        const dispatch = useDispatch()
        function incremanter()
            {
                dispatch(action_incremant())
            }
        function decremanter()
            {
                dispatch(action_decremant())
            }
        return(
            <div>
                <br/>
                <br/>
                <table>
                    <tr>
                        <td>ID</td>
                        <td>Nom</td>
                        <td>Fillier</td>
                    </tr>
                    <tr>
                        {listes.map((e)=><td>{e.nom}</td>)}
                    </tr>
                </table>
                <button onClick={()=>incremanter()}>Incremanter</button>
                <button onClick={()=>decremanter()}>Decremanter</button>
                <br/>
                <br/>
                <Link to="/Afficher">
                        Afficher compteur
                </Link>
            </div>
        )
    }
