import React from "react";
import{REMOVE_INSCRIPTION_action,EDIT_INSCRIPTION_action} from '../Actions/actions';
import { useSelector } from "react-redux";
import {Link } from 'react-router-dom';
import {useDispatch} from'react-redux';

export default function AfficherInsc()
    {
        const listes = useSelector(state =>state)
        const dispatch = useDispatch()
        function Supprimer(e)
        {
            dispatch(REMOVE_INSCRIPTION_action(e.target.value))
            alert("L'inscription bien Supprimer !")
        }
        return(
            <div>
                <br/>
                <br/>
                <table border="1">
                    <tr>
                        <td>ID</td>
                        <td>Nom</td>
                        <td>Fillier</td>
                        <td colSpan="2">Action</td>
                    </tr>
                        {listes.map((e)=>
                                        <tr>
                                            <td>{e.id}</td>
                                            <td>{e.nom}</td>
                                            <td>{e.fillier}</td>
                                            <td> <button value={e.id} onClick={(e)=> Supprimer(e)}>Supprimer</button> </td>
                                            <td> <Link to={`/EditInscr/${e.id}`}> <button>Edit</button> </Link> </td>
                                        </tr>
                                        )}
                </table>
                <br/>
                <h3>{listes.length}</h3>
                <Link to="/AddInscr">
                    Add Inscription
                </Link>
            </div>
        )
    }
