import React,{useState} from "react";
import {useDispatch} from'react-redux';
import{action_incremant,action_decremant} from './../Actions/actions';
import {Link } from 'react-router-dom';

export default function Counter()
    {
        const [pasInc,setpasInc] = useState(1)
        const [pasDec,setpasDec] = useState(1)
        const dispatch = useDispatch()
        function incremanter()
            {
                dispatch(action_incremant(pasInc))
            }
        function decremanter()
            {
                dispatch(action_decremant(pasDec))
            }
        return(
            <div>
                <br/>
                    <label>Pas Incremanter </label>
                    <input type="text" onChange={(e)=>setpasInc(parseInt(e.target.value))}/>
                <br/>
                <button onClick={()=>incremanter()}>Incremanter {pasDec} </button>
                <br/>
                    <label>Pas Decremanter </label>
                    <input type="text" onChange={(e)=>setpasDec(parseInt(e.target.value))}/>
                <br/>
                <button onClick={()=>decremanter()}>Decremanter {pasDec} </button>
                <br/>
                <br/>
                <Link to="/Afficher">
                        Afficher compteur
                </Link>
            </div>
        )
    }
