import React, { useState } from 'react';
import {Link, Navigate } from 'react-router-dom';
import {useDispatch} from'react-redux';
import{ADD_INSCRIPTION_action} from '../Actions/actions';
import { useNavigate } from "react-router-dom";

export default function AddInscr(){
    const [id,setID]=useState("")
    const [nom,setNom]=useState("")
    const [fillier,setFiliere] =useState("")
    const dispatch = useDispatch()
    const Navigate = useNavigate
        function Ajouter()
        {
            dispatch(ADD_INSCRIPTION_action({id:id,nom:nom,fillier:fillier}))
            alert("L'inscription bien Ajouter !")
            Navigate('/')
        }
    return(
        <div>
            <form>
                <input type="text" onChange={(e)=>setID(e.target.value)} />
                <input type="text" onChange={(e)=>setNom(e.target.value)} />
                <input type="text" onChange={(e)=>setFiliere(e.target.value)} />
                <button onClick={()=>Ajouter()}> Ajouter</button>
            </form>
            <br/><br/>
            <Link to="/">
                    return
            </Link>
        </div>
    )
}
