import{ADD_STAGIAIRE,REMOVE_STAGIAIRE,EDIT_STAGIAIRE} from '../Actions/actions';
const Stagiaires = [
							{
								idstg :1, nom : "drai",prenom :"badr",
								adresse : "Guiliz Marrakech",Téléphone : "+212647142558",
								Filière : "FUS", Groupe : "FUS203",
								Compte : {login : "drai",password : "badr"},
							},
							{
								idstg :2, nom : " azzouzi",prenom :"Karim",
								adresse :"Guiliz Marrakech",Téléphone : "+212610203625", 
								Filière : "FUS", Groupe : "FUS203",
								Compte : {login : "azzouzi", password : "karim"},
							}
					]
export function StagiaireddsRed(state=Stagiaires,action)
{
	switch(action.type)
	{
		case ADD_STAGIAIRE :
			return [...state,action.payload];
		case REMOVE_STAGIAIRE :
			return state.filter((elm)=> elm.id != action.payload);
		case EDIT_STAGIAIRE :
			var listes =  state.find((elm)=> elm.id == action.payload.id)
			listes.nom = action.payload.nom 
			listes.fillier = action.payload.fillier
			return state
		default :
			return state;
	}
}
