import React, { useState } from "react";


export default function Minuterie()
    {
        const [Afficher,setAfficher] = useState(0);
        var stop = false;
        function Start()
            {
                alert(Afficher)
                stop = true;
                setAfficher(1) 
                while ( stop != true) {
                    alert("KK") 
                    stop = false;
                  }
                
                alert(Afficher)
            }
        function Areter()
            {
                stop = false
            }
        return(
            <table>
                <h1> Somme: </h1>
                <tr>
                    <td></td>
                    <td>{Afficher}</td>
                </tr>
                <tr>
                    <td><input type="submit" onClick={()=>Start()} value="start"/></td>
                    <td><input type="submit" onClick={()=>Areter()} value="Arrete"/></td>
                </tr>
            </table>
        )
    }
