import React from "react";
import {useDispatch} from'react-redux';
import { useSelector } from "react-redux";
import{action_incremant ,action,action_decremant} from './../Actions/actions';
import {Link } from 'react-router-dom';

export default function Counter()
    {
        const compteur = useSelector(state =>state.count)
        const dispatch = useDispatch()
        function incremanter()
            {
                dispatch(action_incremant())
            }
        function decremanter()
            {
                dispatch(action_decremant())
            }
        return(
            <div>
                <button onClick={()=>incremanter()}>Incremanter</button>
                <button onClick={()=>decremanter()}>Decremanter</button>
                <br/>
                <br/>
                <Link to="/Afficher">
                        Afficher compteur
                </Link>
            </div>
        )
    }
