import React, { useState } from 'react';
import {useDispatch} from'react-redux';
import{REMOVE_INSCRIPTION_action,ADD_INSCRIPTION_action} from '../Actions/actions';
import { useNavigate } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import { useSelector } from "react-redux";

export default function EditInscr(){
    const Afficher = listes.find((elm)=> elm.id == id)
    const [newid,setID]=useState("")
    const [nom,setNom]=useState(Afficher.nom)
    const {id} = useParams()
    const [fillier,setFiliere] =useState("")

    const navigate = useNavigate()
    const dispatch = useDispatch()
    const listes = useSelector(state =>state)
    
        function Ajouter(e)
        {
            e.preventDefault();
            dispatch(REMOVE_INSCRIPTION_action(id))
            dispatch(ADD_INSCRIPTION_action({id:id,nom:nom,fillier:fillier}))
            alert("bien Update !")
            navigate('/')
        }
    return(
        <div>
            <form onSubmit={(e)=>Ajouter(e)}>
                <input type="text" value="${Afficher.id}" onChange={(e)=>setID(e.target.value)} />
                <input type="text" value={nom} onChange={(e)=>setNom(e.target.value)} />
                <input type="text" value={Afficher.fillier} onChange={(e)=>setFiliere(e.target.value)} />
                <input type="submit" />
            </form>
            <br/><br/>
        </div>
    )
}
