const listInscription = []
export function countReducer(state=listInscription,action)
{
	switch(action.type)
	{
		case ADD_INSCRIPTION :
			return [...listInscription,action.payload];
		case REMOVE_INSCRIPTION :
			return listInscription.filter((e)=> e.id !== action.payload.id);
		case EDIT_INSCRIPTION :
			return [...listInscription.filter((e)=> e.id !== action.payload.id),{id,nom,fillier}];
		default :
			return listInscription;
	}
}
