import {Link } from 'react-router-dom';
import { useSelector } from "react-redux";

export default function Counter()
    {
        const listes = useSelector(state =>state)
        const dispatch = useDispatch()
        return(
            <div>
                <br/>
                <table>
                    <tr>
                        <td>ID</td>
                        <td>Nom</td>
                        <td>Fillier</td>
                    </tr>
                        {listes.map((e)=>
                                        <tr>
                                            <td>{e.nom}</td>
                                            <td> <button onClick={()=>incremanter()}>Supprimer</button> </td>
                                            <td> <button onClick={()=>incremanter()}>Edit</button> </td>
                                        </tr>
                                        )}
                </table>
            </div>
        )
    }
