import React, { useState,useEffect } from "react";


export default function Minuterie(props)
    {
        const [Afficher,setAfficher] = useState(props.Nmb);
        const [Etat,setEtat] = useState(false);
        
        useEffect(() => {
            if(Etat == true){
                setTimeout(() => {
                setAfficher(Afficher+1)
              }, 1000);
            }
          });
        function Start()
            {
                {Afficher != props.Nmb?(setAfficher(props.Nmb)):(<></>) }
                setEtat(true)
            }
        function Areter()
            {
                setEtat(false)
            }
        return(
            <table>
                <h1> Somme: </h1>
                <tr>
                    <td></td>
                    <td>{Afficher}</td>
                </tr>
                <tr>
                    <td><input type="submit" onClick={()=>Start()} value="start"/></td>
                    <td><input type="submit" onClick={()=>Areter()} value="Arrete"/></td>
                </tr>
            </table>
        )
    }
