import React, { useState } from 'react';
import {useDispatch} from'react-redux';
import { useSelector } from "react-redux";
import{REMOVE_STAGIAIRE_action} from '../Actions/actions';

export default function DeleteStagiaire(){
    const dispatch = useDispatch()
    const listes = useSelector(state =>state)
    const [idstg,setidstg] =useState("")
        function Supprimer(e)
            {
                var stagiaire = listes.find((elm)=> elm.idstg == idstg)
                (stagiaire == undefined)
                ?(alert("Stagiaire pas trouver."))
                :(<>{
                    dispatch(REMOVE_STAGIAIRE_action(idstg))
                }</>)
            }
    return(
        <div>
                <input type="text" onChange={(e)=>setidstg(e.target.value)} />
                <input type="submit" onClick={()=>Supprimer()} />
                <br/><br/>
        </div>
    )
}
