import React,{useState} from "react";

export default function Forms(props)
    {
        const [Libelle,setLibelle] = useState("")
        const [Prix,setPrix] = useState("")
        const [Quantite, setQuantite] = useState("")
    function Add(e)
        {
            e.preventDefault();
            props.add({Libelle:Libelle,Prix:Prix,Quantite:Quantite})
        }
    return (
    <div>
        <h1>Gestion de factures</h1>
        <form onSubmit={(e)=>Add(e)}>
            <label> Libelle : </label>
            <input type="text" onChange={(e)=>setLibelle(e.target.value)}/><br/>
            <label> Prix : </label>
            <input type="text" onChange={(e)=>setPrix(e.target.value)}/><br/>
            <label> Quantite : </label>
            <input type="text" onChange={(e)=>setQuantite(e.target.value)}/><br/>
            <input type="submit"/>
        </form>
    </div>
    )
}
