import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate } from 'react-router-dom'
import { REMOVE_INSCRIPTION_ACTION } from '../Actions/actions'
import { Link } from 'react-router-dom';
export default function AccueilInscription() {
    const mesInscriptions = useSelector((data) => { return data })
    const disptach = useDispatch()
    const navigate = useNavigate()
    const styletd = { borderWidth: '1px', borderStyle: 'solid', borderColor: 'black' }
    function SupprimerInscription(id) {
        disptach(REMOVE_INSCRIPTION_ACTION(id))
        navigate('/');
    }
    return (
        <div>
            <h1>Liste de component1 : </h1>
            <Link to="/add-inscription">
                <button>add inscription</button>
            </Link>

            <table style={{ width: '100%', height: 'auto', border: '1px solid black' }}>
                <thead>
                    <tr><th>ID</th><th>NOM</th><th>FILIERE</th><th>ACTIONS</th></tr>
                </thead>
                <tbody>
                    {mesInscriptions.map((inscription, index) =>
                        <tr key={index}>
                            <td style={styletd}>{inscription.id}</td>
                            <td style={styletd}>{inscription.nom}</td>
                            <td style={styletd}>{inscription.filiere}</td>
                            <td style={styletd}>
                                <button
                                    onClick={() => SupprimerInscription(inscription.id)}>
                                    Supprimer
                                </button>

                                <Link to={`/edit-inscription/${inscription.id}`}>
                                    <button>Edit</button>
                                </Link>                       
                                 </td>
                        </tr>
                    )}
                </tbody>
            </table>
          

        </div>
    )
}
