import React, { useState } from "react";


export default function Calculer()
    {
        const [a,setA] = useState(0);
        const [b,setB] = useState(0);
        const [OP,setOP] = useState("");
        const [resultat,setResultat] = useState(0);
        function Calculer()
            {
                setResultat(a , OP , b)
            }
        return(
            <table>
                <h1> Somme: </h1>
                <tr>
                    <td><label> a : </label></td>
                    <td><input type="text" onChange={(e)=>setA(parseFloat(e.target.value))}/></td>
                </tr>
                <tr>
                    <td></td>
                    <td> 
                        + : <input type="radio" value="+" name="OP" onChange={(e)=>setOP(String(e.target.value))}/>
                        - : <input type="radio" value="-" name="OP" onChange={(e)=>setOP(e.target.value)}/>
                        * : <input type="radio" value="*" name="OP" onChange={(e)=>setOP(e.target.value)}/>
                        / : <input type="radio" value="/" name="OP" onChange={(e)=>setOP(e.target.value)}/>
                    </td>
                </tr>
                <tr>
                    <td><label> b : </label></td>
                    <td><input type="text" onChange={(e)=>setB(parseFloat(e.target.value))}/></td>
                </tr>
                <tr>
                    <td><label> R : </label></td>
                    <td><input type="text" value={resultat} disabled/></td>
                </tr>
                <tr>
                    <td></td>
                    <td><input type="submit" onClick={()=>Calculer()}/></td>
                </tr>
            </table>
        )
    }
