import React, { useState,useEffect } from "react";


export default function Minuterie()
    {
        const [Afficher,setAfficher] = useState(0);
        const [time,setTime] = useState(false);
        useEffect(() => {
            let timer = null;
            if(time == true){
              timer = setInterval(() => {
                setAfficher(Afficher+1)
              }, 1000);
            }
            return () => {
              clearInterval(timer);
            };
          });
        function Start()
            {
                setTime(true)
            }
        function Areter()
            {
                setTime(false)
            }
        return(
            <table>
                <h1> Somme: </h1>
                <tr>
                    <td></td>
                    <td>{Afficher}</td>
                </tr>
                <tr>
                    <td></td>
                    <td><input type="text" onChange={(e)=>setAfficher(e.target.value)}/></td>
                </tr>
                <tr>
                    <td><input type="submit" onClick={()=>Start()} value="start"/></td>
                    <td><input type="submit" onClick={()=>Areter()} value="Arrete"/></td>
                </tr>
            </table>
        )
    }
