import React from 'react'
import { useState } from 'react'
import { useDispatch,useSelector } from 'react-redux'
import { useNavigate } from 'react-router-dom'
import {EDIT_INSCRIPTION_ACTION} from './../Actions/actions'
import { useParams } from 'react-router-dom';

export default function EditInscription() {
const {id}=useParams();
  const mesInscriptions = useSelector(state=>state)
  const EditInscription  =mesInscriptions.find(elm=>elm.id==id);
  const [InputId,setInputId]=useState(EditInscription.id)
  const [nom,setNom]=useState(EditInscription.nom)
  const [filiere,setFiliere]=useState(EditInscription.filiere)
  const dispatch = useDispatch()
  const navigate=useNavigate()
  const Enregistrer = (e) => {         
          dispatch(EDIT_INSCRIPTION_ACTION({id:InputId,nom:nom,filiere:filiere}))
          navigate('/');          
  }
  return (
    <div>
         <h1>Add Inscription : </h1>
         <form>
            <table>
              <tbody>
                  <tr><td><label>ID:</label></td>
                    <td><input type="text" disabled={true} value={InputId} onChange={(e)=>setInputId(e.target.value)}/></td>
                 </tr>
                 <tr><td><label>Nom:</label></td>
                    <td><input type="text" value={nom} onChange={(e)=>setNom(e.target.value)}/></td>
                 </tr>
                 <tr><td><label>filière:</label></td>
                    <td><input type="text" value={filiere} onChange={(e)=>setFiliere(e.target.value)}/></td>
                 </tr>
                 <tr>
                    <td></td>
                    <td><button onClick={Enregistrer} >Enregistrer</button></td>
                 </tr>
              </tbody>                
            </table>
         </form>
    </div>
  )
}
