import{ADD_INSCRIPTION,REMOVE_INSCRIPTION,EDIT_INSCRIPTION} from '../Actions/actions';
const listInscription = []
export function InscriptionRed(state=listInscription,action)
{
	switch(action.type)
	{
		case ADD_INSCRIPTION :
			return [...state,action.payload];
		case REMOVE_INSCRIPTION :
			return state.filter((elm)=> elm.id != action.payload);
		case EDIT_INSCRIPTION :
			var listes =  state.find((elm)=> elm.id == action.payload.id)
			listes.nom = action.payload.nom 
			listes.fillier = action.payload.fillier
			return state
		default :
			return listInscription;
	}
}
